import { useEffect, useRef, useState } from 'react'

import classes from './AvailableMeals.module.css'
import MealItem from './MealItem/MealItem'
import Card from '../UI/Card'
import Cart from '../Cart/Cart'

// const allCateg = ['choc', 'anan', 'baban', 'deden', 'cocuk']

const DUMMY_MEALS = [
  {
    id: 'm1',
    name: 'Sushi',
    description: 'Finest fish and veggies',
    price: 22.99,
    category: ['choc'],
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm2',
    name: 'Schnitzel',
    description: 'A german specialty!',
    price: 16.5,
    category: ['anan'],
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm3',
    name: 'Barbecue Burger',
    description: 'American, raw, meaty',
    price: 12.99,
    category: ['baban'],
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm4',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: ['deden'],
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm5',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: ['choc'],
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm6',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: ['deden'],
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm7',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: ['cocuk'],
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm8',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: ['cocuk'],
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
]

const AvailableMeals = (props) => {
  const [searchValue, setSearchValue] = useState('')
  const [currentMeals, setCurrentMeals] = useState(DUMMY_MEALS)
  const searchInputRef = useRef()

  useEffect(() => {
    const timer = setTimeout(() => {
      getMealsWithSearchBox()
    }, 500)
    return () => {
      clearTimeout(timer)
    }
  }, [searchValue])

  const onChangeInput = () => {
    setSearchValue(searchInputRef.current.value)
  }

  const getMealsWithSearchBox = () => {
    const newMeals = currentMeals
      .filter((meal) => meal.name.includes(searchValue))
      .map((i) => {
        return {
          id: 'm1',
          name: 'Sushi',
          description: 'Finest fish and veggies',
          price: 22.99,
          category: ['choc'],
          altPic:
            'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
        }
      })
  }

  const mealsList = currentMeals.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
      imagesrc={meal.altPic}
    />
  ))

  return (
    <section className={classes.meals}>
      <Card className={classes.cardSection}>
        <div className={classes.inputText}>
          <label htmlFor="input_text_availablemeals">Arama</label>
          <input
            id="input_text_availablemeals"
            onChange={onChangeInput}
            ref={searchInputRef}
          />
        </div>
        <ul>{mealsList}</ul>
      </Card>
      {props.cartIsShown && <Cart onClose={props.onClose} />}
    </section>
  )
}

export default AvailableMeals
