import classes from './AvailableMeals.module.css'
import MealItem from './MealItem/MealItem'
import Card from '../UI/Card'
import Cart from '../Cart/Cart'

const allCateg = ['choc', 'anan', 'baban', 'deden', 'cocuk']

const DUMMY_MEALS = [
  {
    id: 'm1',
    name: 'Sushi',
    description: 'Finest fish and veggies',
    price: 22.99,
    category: ['choc'],
  },
  {
    id: 'm2',
    name: 'Schnitzel',
    description: 'A german specialty!',
    price: 16.5,
    category: ['anan'],
  },
  {
    id: 'm3',
    name: 'Barbecue Burger',
    description: 'American, raw, meaty',
    price: 12.99,
    category: ['baban'],
  },
  {
    id: 'm4',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: ['deden'],
  },
  {
    id: 'm5',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: ['choc'],
  },
  {
    id: 'm6',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: ['deden'],
  },
  {
    id: 'm7',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: ['cocuk'],
  },
  {
    id: 'm8',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: ['cocuk'],
  },
]

const AvailableMeals = (props) => {
  const mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ))

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
      {props.cartIsShown && <Cart onClose={props.onClose} />}
    </section>
  )
}

export default AvailableMeals
