import { useEffect, useRef, useState } from 'react'

import classes from './AvailableMeals.module.css'
import MealItem from './MealItem/MealItem'
import Card from '../UI/Card'
import Cart from '../Cart/Cart'

const allCateg = [
  { name: 'choc', id: 'ca1' },
  { name: 'anan', id: 'ca2' },
  { name: 'baban', id: 'ca3' },
  { name: 'deden', id: 'ca4' },
  { name: 'cocuk', id: 'ca5' },
]

const DUMMY_MEALS = [
  {
    id: 'm1',
    name: 'Sushi',
    description: 'Finest fish and veggies',
    price: 22.99,
    category: 'choc',
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm2',
    name: 'Schnitzel',
    description: 'A german specialty!',
    price: 16.5,
    category: 'anan',
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm3',
    name: 'Barbecue Burger',
    description: 'American, raw, meaty',
    price: 12.99,
    category: 'baban',
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm4',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: 'deden',
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm5',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: 'choc',
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm6',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: 'deden',
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm7',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: 'cocuk',
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
  {
    id: 'm8',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
    category: 'cocuk',
    altPic:
      'https://cdn.discordapp.com/attachments/833414619254620260/999681032796241920/zorrtatak.png',
  },
]

const AvailableMeals = (props) => {
  const [searchValue, setSearchValue] = useState('')
  const [currentMeals, setCurrentMeals] = useState(DUMMY_MEALS)
  const searchInputRef = useRef()

  const [checked, setChecked] = useState([])

  useEffect(() => {
    const timer = setTimeout(() => {
      getMealsWithSearchBox()
    }, 500)
    return () => {
      clearTimeout(timer)
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [searchValue])

  const onChangeInput = () => {
    setSearchValue(searchInputRef.current.value)
  }

  const getMealsWithSearchBox = () => {
    // filtre varsa categori varsa
    // filtre varsa categori yoksa
    // filtre yoksa categori varsa
    // filtre yoksa categori yoksa
    let filterMeals
    if (searchValue.length > 0 && checked.length > 0) {
      // 1
      filterMeals = currentMeals
    } else if (
      // 2
      searchValue.length > 0 &&
      checked.length === 0
    ) {
      setCurrentMeals(DUMMY_MEALS)
      filterMeals = currentMeals
    } else if (
      // 3
      searchValue.length === 0 &&
      checked.length > 0
    ) {
      setCurrentMeals(DUMMY_MEALS)
      getMealsWithCategories()
      return
    } else if (
      // 4
      searchValue.length === 0 &&
      checked.length === 0
    ) {
      setCurrentMeals(DUMMY_MEALS)
      return
    } else if (checked.length > 1) {
      filterMeals = DUMMY_MEALS
    }
    const newMeals = filterMeals
      .filter((meal) =>
        meal.name.toLowerCase().includes(searchValue.toLowerCase())
      )
      .map((i) => {
        return {
          id: i.id,
          name: i.name,
          description: i.description,
          price: i.price,
          category: i.category,
          altPic: i.altPic,
        }
      })
    setCurrentMeals(newMeals)
  }

  const getMealsWithCategories = () => {
    //  arama yapilmissa ve filtreleme varsa 1
    // arama yapilmissa ve filtreleme yoksa 2
    // arama yapilmamissa ve filtreleme varsa 3
    // arama yapilmamissa ve filtreleme yoksa 4
    let searchingMeal
    if (searchValue.length > 0 && checked.length > 0) {
      // 1
      searchingMeal = currentMeals
    } else if (
      // 2
      searchValue.length > 0 &&
      checked.length === 0
    ) {
      setCurrentMeals(DUMMY_MEALS)
      return
    } else if (
      // 3
      searchValue.length === 0 &&
      checked.length > 0
    ) {
      searchingMeal = DUMMY_MEALS
    } else if (
      // 4
      searchValue.length === 0 &&
      checked.length === 0
    ) {
      setCurrentMeals(DUMMY_MEALS)
      return
    } else if (checked.length > 1) {
      searchingMeal = DUMMY_MEALS
    }
    console.log(searchingMeal)

    const newS = searchingMeal
      .filter((meal) => checked.includes(meal.category))
      .map((i) => {
        return {
          id: i.id,
          name: i.name,
          description: i.description,
          price: i.price,
          category: i.category,
          altPic: i.altPic,
        }
      })
    setCurrentMeals(newS)
  }

  useEffect(() => {
    getMealsWithCategories()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [checked])

  const handleCheck = (event) => {
    var updatedList = [...checked]
    if (!checked.includes(event.target.name)) {
      updatedList = [...checked, event.target.name]
      setChecked(updatedList)
    } else {
      const indexOfEventTargetName = updatedList.indexOf(event.target.name)
      updatedList.splice(indexOfEventTargetName, 1)
      setChecked(updatedList)
    }
  }

  const categList = allCateg.map((name, index) => {
    return (
      <li key={index}>
        <input
          type="checkbox"
          onChange={handleCheck}
          id={`custom-checkbox-${index}`}
          name={name.name}
        />
        <label htmlFor={`custom-checkbox-${index}`}>{name.name}</label>
      </li>
    )
  })
  const mealsList = currentMeals.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
      imagesrc={meal.altPic}
    />
  ))

  return (
    <section className={classes.meals}>
      <Card className={classes.cardSection}>
        <div
          style={{ width: props.cartIsShown ? '60%' : '100%' }}
          className={classes.leftSection}
        >
          <div className={classes.filterSection}>
            <div className={classes.inputText}>
              <label htmlFor="input_text_availablemeals">Arama</label>
              <input
                ref={searchInputRef}
                id="input_text_availablemeals"
                onChange={onChangeInput}
              />
            </div>
            <ul className={classes.altForm}>{categList}</ul>
          </div>
          <ul className={classes.mealsList}>{mealsList}</ul>
        </div>
        {props.cartIsShown && (
          <Cart className={classes.rightSection} onClose={props.onClose} />
        )}
      </Card>
    </section>
  )
}

export default AvailableMeals
